package com.servinglynk.hmis.warehouse.notification.scheduler.dao;import java.util.List;import org.springframework.transaction.annotation.Transactional;import com.servinglynk.hmis.warehouse.notification.persistence.dao.core.QueryExecutor;import com.servinglynk.hmis.warehouse.notification.persistence.entity.WorkerHeaderEntity;@Transactionalpublic class WorkerHeaderDao extends QueryExecutor implements IWorkerHeaderDao{		public List<WorkerHeaderEntity> findByExternalId(String externalId) {		WorkerHeaderEntity workerHeaderEntity = new WorkerHeaderEntity();		workerHeaderEntity.setExternalId(externalId);		workerHeaderEntity.setMaxRetry(1);		workerHeaderEntity.setRetryInterval(1);		workerHeaderEntity.setStatus("ACTIVE");		return (List<WorkerHeaderEntity>) findByExample(workerHeaderEntity);	}}