JOIN enrollment enrollment ON (client.personalid = enrollment.personalid)
JOIN project project ON (enrollment.projectentryid = project.projectentryid)
JOIN organization organization ON (project.organizationid = organization.organizationid)
LEFT OUTER JOIN lastpermanentaddress lastpermanentaddress ON (enrollment.projectentryid = lastpermanentaddress.projectentryid)
LEFT OUTER JOIN disabilities disabilities ON (enrollment.projectentryid = disabilities.projectentryid)
LEFT OUTER JOIN exit exit ON (enrollment.projectentryid = exit.projectentryid)
LEFT OUTER JOIN exithousingassessment exithousingassessment ON (exit.exitid = exithousingassessment.exitid)
LEFT OUTER JOIN domesticviolence domesticviolence ON (enrollment.projectentryid = domesticviolence.projectentryid);
